import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Landing from "./components/Landing";
import Signup from "./components/Signup";
import Signin from "./components/Signin";
import Dashboard from "./components/Dashboard";
import AuctionItem from "./components/AuctionItem";
import PostAuction from "./components/PostAuction";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    setIsAuthenticated(!!token);
  }, []);

  return (
    <Router>
      <Navbar isAuthenticated={isAuthenticated} />
      <main className="container mt-4">
      <header className="bg-light shadow-sm py-3">
  <div className="container d-flex justify-content-between align-items-center">
    <h1 className="fw-bold">Auction App</h1>
    <nav>
      <Link to="/dashboard" className="mx-3 text-dark text-decoration-none">Dashboard</Link>
      <Link to="/post-auction" className="mx-3 text-dark text-decoration-none">Post Auction</Link>
      {!isAuthenticated ? (
        <>
          <Link to="/signup" className="btn btn-outline-primary mx-2">Sign Up</Link>
          <Link to="/signin" className="btn btn-primary mx-2">Sign In</Link>
        </>
      ) : (
        <button onClick={handleLogout} className="btn btn-danger">Logout</button>
      )}
    </nav>
  </div>
</header>
        <Routes>
          <Route path="/" element={<Landing />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/signin" element={<Signin />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/auction/:id" element={<AuctionItem />} />
          <Route path="/post-auction" element={<PostAuction />} />
        </Routes>
      </main>
      <Footer />
    </Router>
  );
}

export default App;
