import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function Dashboard() {
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState('all');
  const [search, setSearch] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/signin'); // Redirect to signin if not authenticated
      return;
    }

    const fetchItems = async () => {
      try {
        const res = await axios.get('http://localhost:3001/auctions');
        setItems(res.data);
      } catch (error) {
        console.error('Error fetching auctions:', error);
      }
    };

    fetchItems();
    const interval = setInterval(fetchItems, 5000); // Refresh every 5 sec
    return () => clearInterval(interval); // Cleanup interval
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/signin'); // Redirect to Signin
  };

  const filteredItems = items
    .filter((item) => filter === 'all' || (filter === 'open' ? !item.isClosed : item.isClosed))
    .filter((item) => item.itemName.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2 className="text-primary">Auction Dashboard</h2>
        <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
      </div>

      {/* Filters & Search */}
      <div className="row mb-3">
        <div className="col-md-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search items..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>
        <div className="col-md-4">
          <select className="form-select" onChange={(e) => setFilter(e.target.value)}>
            <option value="all">All Auctions</option>
            <option value="open">Open Auctions</option>
            <option value="closed">Closed Auctions</option>
          </select>
        </div>
        <div className="col-md-4 text-end">
          <Link to="/post-auction">
            <button className="btn btn-success">Post New Auction</button>
          </Link>
        </div>
      </div>

      {/* Auction List */}
      <ul className="list-group">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <li key={item._id} className={`list-group-item d-flex justify-content-between ${item.isClosed ? 'bg-secondary text-white' : ''}`}>
              <Link to={`/auction/${item._id}`} className="text-decoration-none text-dark fw-bold">
                {item.itemName} - ${item.currentBid} {item.isClosed ? '(Closed)' : ` - ${item.bids?.length || 0} Bids`}
              </Link>
            </li>
          ))
        ) : (
          <p className="text-center text-muted">No auctions found.</p>
        )}
      </ul>
    </div>
  );
}

export default Dashboard;
